function menuAusgabe2(){
/* Changes second menu according to the choice of the version (normalisierte Lesefassung,
OCR Version, Faksimile) */
/* Input: resource/json/output.json , generated by documentToJson.xsl 
Ouput: generated second menu with class json2 */

    $(function () {
        $(".ausgabe2").change(function () {       
            var $dropdown = $(this);
            
            //takes json-data 
            $.getJSON("resources/json/output4.json", function (data) {
                var key = $dropdown.val();
                var vals =[];
                
                switch (key) {
                    case 'GW5041':
                    vals = data.GW5041.split(",");
                    break;
                    case 'GW5042':
                    vals = data.GW5042.split(",");
                    break;
                    case 'GW5046':
                    vals = data.GW5046.split(",");
                    break;
                    case 'GW5047':
                    vals = data.GW5047.split(",");
                    break
                }
                
                var $kapitel = $(".kapitel2");             
                $kapitel.empty();
                
                //creates first empty option. Chosen select will fill it with data-placeholder from synopsis.html
                $kapitel.append("<option value=''></option>")
                $.each(vals, function (index, value) {
                    
                    var para = /P[0-9]+/
                    if (value.match(para)){
                        $kapitel.append("<option value=" + value.replace(/(GW[0-9]+)[PB][0-9]+(n[0-9]+)/g, "$1$2") + ">Paratext " + value.replace(/GW[0-9]+[PB]([0-9]+)n[0-9]+/g, "$1") + "</option>");
                    }
                    else{
                        //Creates a new value for select menu with regular expression
                        $kapitel.append("<option value=" + value.replace(/(GW[0-9]+)[PB][0-9]+(n[0-9]+)/g, "$1$2") + ">Kapitel " + value.replace(/GW[0-9]+[PB]([0-9]+)n[0-9]+/g, "$1") + "</option>");
                    }                  
                });
                
                //chosen select needs an updated trigger so that new data will be visible in the browser
                $kapitel.trigger("chosen:updated");
            });
        });
})};

